const fs = require("fs");
class Complier {
    constructor(config) {
        // 保存配置文件
        this.config = config;
        // 保存模块依赖关系
        this.modules = {};
    };
    run() {
        this.buildModule();
    };
    // 将依赖关系保存到this.modules之中
    buildModule() {
        let code = this.getSource();
        this.modules[this.config.entry] = code;
    };
    // 读取文件内容
    getSource() {
        return fs.readFileSync(this.config.entry, "utf8");
    }

}

module.exports = Complier;